programs.vertex:
#version 100

attribute vec3 v_Position;

uniform mat4 modelViewProjection;

uniform struct {
    int step;
    float near;
    float far;
    float aspect;
    float fov;

} trans;

void main() {
    gl_Position = modelViewProjection * vec4(v_Position, 1);
}

programs.fragment:
#version 100

precision mediump float;

void main() {
    gl_FragColor = vec4(0.5, 0.5, 0.5, 1);
}

javascript:
function ui(c) {
    var ui = c.ui;

    // add left hand side grid
    ui.add(ui.grid({
        children: [
            // theta slider in range [0, pi]
            ui.label({ value: 'Steps:', title: 'Steps' }),
            ui.slider({
                bind: [this.trans, 'step'],
                min: 0,
                max: 4
            }),
            // phi slider in range [0, 2pi]
            ui.label({ value: 'fov:', title: 'fov' }),
            ui.slider({
                bind: [this.trans, 'fov'],
                min: 0.0,
                max: 2.0 * Math.PI
            })
        ]
    }), { left: 10, bottom: 6 });

    // add right hand side grid
    ui.add(ui.grid({
        children: [
            // line thickness slider in range [0, 0.1]
            ui.label({ value: 'far:', title: 'Outer Contour Thickness' }),
            ui.slider({
                bind: [this.trans, 'far'],
                min: 0.1,
                max: 10.0
            }),
            // discretization steps slider in range [2, 10]
            // this yields a float so we have to floor it in order to yield an integer later
            ui.label({ value: 'aspect:', title: 'Number of discretization steps' }),
            ui.slider({
                bind: [this.trans, 'aspect'],
                min: 0.1,
                max: 10.0
            })
        ]
    }), { right: 10, bottom: 6 });
}

function init(c) {
    this.box = new c.models.Box(c, 1, 1, 1);
    this.view = c.models.View.perspective(c, 45, null, 0.01, 50);

    this.view.transform
        .rotateY(-Math.PI / 6)
        .rotateX(-Math.PI / 6)
        .translateForward(3);
    this.trans = { step: 4, near: 1, far: 3, aspect:1.5, fov: Math.PI/4 }
    ui.call(this, c);
}

function render(c) {
    c.view(this.view);
    this.box.render(c);
}
